// Select the buttons and number element using type assertions
const addition = document.getElementById('increment') as HTMLButtonElement | null;
const deduction = document.getElementById('decrement') as HTMLButtonElement | null;
const Test = document.getElementById('number') as HTMLElement ;

let Countint: number = 0;

// Ensure elements exist before adding event listeners
if (addition && deduction ) {
  addition.addEventListener('click', () => {
    Countint += 1;
    Test.innerHTML = Countint.toString(); // Use `textContent` for better type safety
  });

  deduction.addEventListener('click', () => {
    Countint -= 1;
    Test.textContent = Countint.toString();
  });
} else {
  console.error('One or more elements are missing in the DOM.');
}
